scan_table_check.script:
(function (engine) {

	if (x_snc_codesanity.IsCheckApplicableToRecord())
	{
		var grSuites = new GlideRecord('sys_atf_test_suite');
		grSuites.addQuery('name','=',current.name);
		grSuites.query();
		if (grSuites.getRowCount() != 1)
		{
			engine.finding.increment();
		}
	}

})(engine);
