sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '5cd56c001b12e910cd6153d16b4bcbf4';
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_CODESANITY_');

	var strShortScript = '';
	var strLongScript = '';
	var nLine = 1;
	while (nLine <= 100)
	{
		if (nLine <= 10)
		{
			strShortScript += '\tvar nLine = '+nLine+';\n';
		}
		strLongScript += '\tvar nLine = '+nLine+';\n';
		nLine++;
	}
	
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test1',
		'function _ATF_CODESANITY_Test1()\n'
		+'{'
		+strShortScript
		+'}');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test2',
		'function _ATF_CODESANITY_Test2()\n'
		+'{'
		+strLongScript
		+'}');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);
	
	
	var grBR = new GlideRecord('sys_script');
	grBR.sys_scope = 'global';
	grBR.script =
		'(function executeRule(current, previous /*null when async*/) {\n'
		+strShortScript
		+'})(current, previous);';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grBR);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script',strRecordSysId,false);
	
	
	var grBR = new GlideRecord('sys_script');
	grBR.sys_scope = 'global';
	grBR.script =
		'(function executeRule(current, previous /*null when async*/) {\n'
		+strLongScript
		+'})(current, previous);';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grBR);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script',strRecordSysId,true);	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
