sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '31bed7b41bb4e1105b76777d8b4bcb7a';
	
	test.TestCase_InstanceScanCheck(strCheck,'sys_app',x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id,false);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app',x_snc_devtools.GetAppRecord('x_snc_devtools').sys_id,false);
	
	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.name = 'Test';
	var strAppSysId = x_snc_devtools.GlideRecordInsert(grApp);
	
	// No AppInstall script include, no finding
	test.Log('No script include');
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, false);	
	
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.sys_scope = strAppSysId;
	grSI.name = 'AppInstall';
	grSI.script = '';
	var strScriptIncludeSysId = x_snc_devtools.GlideRecordInsert(grSI);

	// An empty AppInstall script include
	test.Log('Empty script include');
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, true);	
	
	// An AppInstall script include with a class
	var strScript = 'var AppInstall = Class.create();\n'
		+'AppInstall.prototype = {\n'
		+'   initialize: function() {\n'
		+'   },\n'
		+'   type: \'AppInstall\'\n'
		+'};';
	test.Log(strScript);
	x_snc_devtools.RecordSetValue('sys_script_include',strScriptIncludeSysId, 'script', strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, true);	
	
	// An AppInstall script include calling another AppInstall() function
	var strScript = 'function AppInstall()\n'
		+'{\n'
		+'   some_scope.AppInstall();\n'
		+'   var installer = InstallerAPI(\'Test\');\n'
		+'   return installer.Finish();\n'
		+'}';
	test.Log(strScript);
	x_snc_devtools.RecordSetValue('sys_script_include',strScriptIncludeSysId, 'script', strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, true);	
	
	// An AppInstall script include calling not returning installer.Finish();
	var strScript = 'function AppInstall()\n'
		+'{\n'
		+'   some_scope.AppInstall();\n'
		+'   var installer = InstallerAPI(\'Test\');\n'
		+'   return;\n'
		+'}';
	test.Log(strScript);
	x_snc_devtools.RecordSetValue('sys_script_include',strScriptIncludeSysId, 'script', strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, true);	

	
	// A well-formed AppInstall script include 
	var strScript = 'function AppInstall()\n'
		+'{\n'
		+'   var installer = InstallerAPI(\'Test\');\n'
		+'   return installer.Finish();\n'
		+'}';
	test.Log(strScript);
	x_snc_devtools.RecordSetValue('sys_script_include',strScriptIncludeSysId, 'script', strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app', strAppSysId, false);	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
