sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '49dfce801b1165105b76777d8b4bcbfe';
	
	
	// Test "CodeSanity - Installation" has test steps
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test','2c6758d3db4a8510bcced03cd3961921',false);
	
	var grTest = new GlideRecord('sys_atf_test');
	grTest.initialize();
	grTest.name = 'TEST1';
	grTest.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	var strTest1SysId = x_snc_devtools.GlideRecordInsert(grTest);
	// Test1 does not have any test steps
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',grTest.sys_id,true);
	
	var grStep = new GlideRecord('sys_atf_step');
	grStep.initialize();
	grStep.active = false;
	grStep.test = strTest1SysId;
	grStep.step_config = '58ab71985f30220012b44adb7f46661e'; // Log
	grStep.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	var strStep1SysId = x_snc_devtools.GlideRecordInsert(grStep);
	// Step1 is not active
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',grTest.sys_id,true);	
	

	var grStep = new GlideRecord('sys_atf_step');
	grStep.initialize();
	grStep.active = true;
	grStep.step_config = '58ab71985f30220012b44adb7f46661e'; // Log
	grStep.test = strTest1SysId;
	grStep.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	var strStep2SysId = x_snc_devtools.GlideRecordInsert(grStep);
	// Step2 is active
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',grTest.sys_id,false);		
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
